mapper.sql:
FROM
    CSVREAD(
    'classpath:database/KEN_ALL.CSV',
    'LOCAL_GOVERNMENT_CODE,OLD_ZIP_CODE,ZIP_CODE,KANA1,KANA2,KANA3,ADDRESS1,ADDRESS2,ADDRESS3',
    'Shift-JIS'
    )



        zip_code LIKE #{escapedZipCode} 



        OR
            address1 LIKE #{escapedAddress} 
        OR
            address2 LIKE #{escapedAddress} 
        OR
            address3 LIKE #{escapedAddress} 
        OR
            address1 || address2 || address3 LIKE #{escapedAddress} 
        OR
            kana1 LIKE #{escapedAddress} 
        OR
            kana2 LIKE #{escapedAddress} 
        OR
            kana3 LIKE #{escapedAddress} 
        OR
            kana1 || kana2 || kana3 LIKE #{escapedAddress}
